import React, { useState } from 'react'
import axios from "axios"
import Balance_Sheet from '../../../ServiceData/Balance_Sheet.xlsx';
import Budget_Sheet from '../../../ServiceData/Balance_Sheet.xlsx';
import fileDownload from 'js-file-download'
import xlsx from 'xlsx'

export default function Update() {

    const [filetoDownload, setfiletoDownload] = useState("")
    const [fileuploadoption, setfileuploadoption] = useState("")
    const [filetoupload, setfiletoupload] = useState("")
    const [error, seterror] = useState("")
    function downloadfile(e) {
        e.preventDefault();
        if (filetoDownload === "") {
            seterror('No file was selected')
        } else {
            seterror('')
            if (filetoDownload === "Balance_Sheet") {
                axios.get(Balance_Sheet, {
                    responseType: 'blob',
                })
                    .then((res) => {
                        fileDownload(res.data, 'Balance_Sheet.xlsx')
                    })
            }
            if (filetoDownload === "Budget_Sheet") {
                axios.get(Budget_Sheet, {
                    responseType: 'blob',
                })
                    .then((res) => {
                        fileDownload(res.data, 'Budget_Sheet.xlsx')
                    })
            }

        }
        // console.log("submit")
    }
    function uploadfile(e) {
        e.preventDefault();
        // 
        if (fileuploadoption === "") {
            seterror("Please select a file")
        } else {
            if (filetoupload === "" || filetoupload === null || filetoupload === undefined) {
                seterror("No file was selected")
            } else {
                if (fileuploadoption === filetoupload.target.files[0].name) {
                    console.log(filetoupload.target.files[0])
                    console.log('upload correct')

                    //do file operations here
                    //--------------------------------------------------
                    const formData=new FormData();
                    formData.append()
                    axios.post('http://localhost:8080/userfileupload', filetoupload)
                        .then(res => {
                            console.log(res.data)
                        })
                    // const file = xlsx.readFile(filetoupload)
                    // const workSheet = file.Sheets['Data']
                    // const data = []
                    // for (const [key, value] of Object.entries(workSheet)) {
                    //     // console.log(value);
                    //     data.push(value.v);
                    // }
                    // const yearOne = {
                    //         "indicator": data[1],
                    //         "email": data[2],
                    //         "year": data[4],
                    //         "Cash": Math.round(data[10],2),
                    //         "Accounts_Receivable": Math.round(data[16],2),
                    //         "Prepaid_expenses": Math.round(data[22],2),
                    //         "Inventory": Math.round(data[28],2),
                    //         "Property_Equipment": Math.round(data[34],2),
                    //         "Goodwill": Math.round(data[40],2),
                    //         "Accounts_Payable": Math.round(data[46],2),
                    //         "Accrued_expenses": Math.round(data[52],2),
                    //         "Unearned_revenue": Math.round(data[58],2),
                    //         "Long_term_debt": Math.round(data[64],2),
                    //         "Other_long_term_liabilities": Math.round(data[70],2),
                    //         "Equity_Capital": Math.round(data[76],2),
                    //         "Retained_Earnings": Math.round(data[82],2),
                    // }
                    // console.log(yearOne)

                    //--------------------------------------------------
                    seterror("")
                } else {
                    seterror(`Please Upload ${fileuploadoption}`)
                }
            }
        }

    }
    return (
        <div className="profile">
            <h1>Hello from Update</h1>
            <div className="myDiv">
                <br />
                <br />
                {/*----------------------------------------*/}
                <h1>1- Step One: Select a template to download</h1>
                <div className="col-sm-6 offset-sm-3">
                    {/*to be  changed later*/}
                    <form onSubmit={downloadfile}>
                        <select
                            onChange={e =>
                                setfiletoDownload(e.target.value)
                            }

                        >
                            <option value="">Select an Option</option>
                            <option value="Balance_Sheet">Balance Sheet</option>
                            <option value="Budget_Sheet">Budget Sheet</option>
                        </select>
                        <input type="submit" value="Download" className="btn btn-primary" />
                    </form>
                </div>
                {/*----------------------------------------*/}

                <br />
                <br />
            </div>
            <br />
            <div className="myDiv">
                <br />
                <br />
                <h1>2- Step Two: Fill up the template and upload</h1>


                <div className="col-sm-6 offset-sm-3">
                    <form onSubmit={uploadfile}>
                        <select
                            onChange={e =>
                                setfileuploadoption(e.target.value)
                            }
                        >
                            <option value="">Select an Option</option>
                            <option value="Balance_Sheet.xlsx">Balance Sheet</option>
                            <option value="Budget_Sheet.xlsx">Budget Sheet</option>
                        </select>
                        <input type="file" id="myFile" name="filename" onChange={e => setfiletoupload(e)} />
                        <input type="submit" className="btn btn-primary" />
                    </form>
                </div>

                <br />
                <br />
            </div>
            <br />
            <br />
            <div>
                {error}
            </div>
            <br />
            <br />
            <br />
            <br />
            <br />
            <br />
        </div>
    )
}
