import React, { useState } from 'react'
import { useHistory } from "react-router-dom"
import Navbar from "../Navbar"
import axios from "axios"
function Login() {
  //-----------------------------------
  const history = useHistory()
  const [error, seterror] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")

  const logIn = e => {
    /*
    catch user data,
    search database for match
    if no match do nothing with warning
    else go to the proper dashboard // validate admin account 
    */
    e.preventDefault();
    let user = {
      "email": email,
      "password": password
    }
    axios.post("http://localhost:8080/users/login", user)
      .then(res => {
        console.log(res)
        const resLen = res.data.length
        if (resLen === 0) {
          seterror('No User Found Please register')
        }else{
          seterror("")
          const resInfo=res.data[0]
          console.log(resInfo)

          //move to dashboard
          
        }
      }

      )

    //history.push('/login')
  }

  //-----------------------------------
  return (
    <div>
      <Navbar />
      <div className="col-sm-6 offset-sm-3">
        <h1>Login Page</h1>
        <input type="text" className="form-control" placeholder="Email" onChange={(e) => setEmail(e.target.value)} value={email} /><br />
        <input type="password" className="form-control" placeholder="Password" onChange={(e) => setPassword(e.target.value)} value={password} /><br />
        <button onClick={logIn} className="btn btn-primary">Login</button>
      </div>
      <br />
      <br />
      <br />
      <h1>{error}</h1>
    </div>
  )
}

export default Login
