import React, { useState } from 'react'
import '../../App.css';
import './Register.css';
//import Popup from 'reactjs-popup';
import Navbar from "../Navbar"
import axios from "axios"
import { useHistory } from "react-router-dom"

export default function Register() {
    //----------------------------------
    const history = useHistory()
    const [error, setError] = useState("")
    useS

    //use states:
    const [fname, setFname] = useState("")
    const [lname, setLname] = useState("")
    const [phone, setPhone] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [repassword, setRepassword] = useState("")

    //sending data to the server
    //this is just a test so we can 
    /*
      async function signUp() {
          let user = { fname, lname, phone, email, password, repassword }
          console.warn(user);
          try {
              //put server URL here
              let result = await fetch(`/user`, {
                  //let result = await fetch(`http://localhost:8080/api/register`, {
                  method: 'post',
                  mode: 'no-cors',
                  headers: {
                      'Accept': 'application/json',
                      'contentType': 'application/json'
                  },
                  body: JSON.stringify({
                      firstName: fname,
                      lastName: lname,
                      PhoneNumber: phone,
                      EmailAddress: email,
                      PasswordUsed: password,
                      rePasswordUsed: repassword
                  })
              })
              console.log(result)
              //result=await result.json()
              localStorage.setItem("user-info", JSON.stringify(result))
              history.push('/')
          } catch (error) {
              console.log(error)
          }
          //localStorage.setItem("user-info",JSON.stringify(result))
      }
  */

    //using axios
    const signUp = e => {
        e.preventDefault();
        let user = {
            "first_name": fname,
            "last_name": lname,
            "phone": phone,
            "email": email,
            "password": password
        }
        if (fname === "" || lname === "" || phone === "" || email === "" || password === "") {
            setError("Please fill all fields")
        } else {
            if (password !== repassword) {
                setError("Passwords does not match")
            } else {
                console.log(user)
                axios.post('http://localhost:8080/user', user)
                    .then(res => {
                        console.log(res)
                        //history.push('/')
                    })
                    .catch(error => {
                        //error.first_name.message
                        //error.last_name.message
                        //error.password.message
                        //error.phone.message
                        //error.email.message
                        let errors={
                            "fname":error.data.errors.first_name.message,
                            "lname":error.data.errors.last_name.message,
                            "email":error.data.errors.email.message,
                            "phone":error.data.errors.phone.message,
                            "password":error.data.errors.password.message
                        }
                        console.log(errors)
                        setError(errors)
                    })
            }
        }

    }
    //----------------------------------

    return (
        <>
            <Navbar />
            <div className="col-sm-6 offset-sm-3">
                <h1>Register Page</h1>
                <input type="text" className="form-control" placeholder="First Name" onChange={(e) => setFname(e.target.value)} value={fname} /><br />
                <input type="text" className="form-control" placeholder="Last Name" onChange={(e) => setLname(e.target.value)} value={lname} /><br />
                <input type="text" className="form-control" placeholder="Phone Number" onChange={(e) => setPhone(e.target.value)} value={phone} /><br />
                <input type="text" className="form-control" placeholder="Email" onChange={(e) => setEmail(e.target.value)} value={email} /><br />
                <input type="password" className="form-control" placeholder="Password" onChange={(e) => setPassword(e.target.value)} value={password} /><br />
                <input type="password" className="form-control" placeholder="Re-Password" onChange={(e) => setRepassword(e.target.value)} value={repassword} /><br />
                <button onClick={signUp} className="btn btn-primary">Sign Up</button>
                <br />
                <br />
                <h1>{error}</h1>
                <br />
                <br />
            </div>
            <div className='sign-up'></div>
        </>
    )
}
