import axios from 'axios'
import React, { useEffect, useState } from 'react'
import moment from 'moment'
import "./ConsultantStyles.css"

export default function DashboardConsultantDetails() {
    const [consultant, setconsultant] = useState("")
    const [consultantId, setconsultantId] = useState("")
    const [consultantRequests, setconsultantRequests] = useState([])

    const [filetoupload, setfiletoupload] = useState("")

    const [seconds, setSeconds] = useState(0);
    const [pageRefresh, setpageRefresh] = useState(0)

    const [error, seterror] = useState("")

    useEffect(() => {
        const user = {
            "email": localStorage.getItem("user")
        }
        const interval = setInterval(() => {
            axios.post('http://localhost:8080/findconsult', user)
                .then(res => {
                    setconsultant(res)
                    setconsultantId({ consultantID: res.data[0]._id })
                    // console.log(consultantId)
                })
            axios.post('http://localhost:8080/findconsultantrequests', consultantId)
                .then(res => {
                    const data = res.data
                    setconsultantRequests([...data])
                    // console.log(consultantRequests)
                })
            setSeconds(seconds => seconds + 1);
        }, 1000);
        return () => clearInterval(interval);

        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [pageRefresh, consultantId])

    function displayconsultantrequests() {
        if (consultantRequests.length === 0) {
            return (
                <h1>No Requests To work ON</h1>
            )
        } else {
            return (
                <table id="customers">
                    <tbody>
                        <tr>
                            <th>User</th>
                            <th>Topic</th>
                            <th>Urgency</th>
                            <th>Description</th>
                            <th>Date</th>
                            <th>Upload Report</th>
                            <th>Action</th>
                        </tr>

                        {
                            consultantRequests.map(data =>
                                <tr key={data._id}>
                                    <td>{data.user}</td>
                                    <td>{data.topic}</td>
                                    <td>{data.urgency}</td>
                                    <td>{data.description}</td>
                                    <td>{moment(data.date).format("LLL")}</td>
                                    <td>
                                        <input type="file" id="myFile" name="filename" onChange={e => setfiletoupload(e)} />
                                    </td>

                                    <td>
                                        <button onClick={postfile(data)} className="btn btn-primary">Post</button>
                                    </td>
                                </tr>
                            )
                        }




                    </tbody>
                </table>
            )
        }
    }


    function postfile(e, data) {
        e.preventDefault();
        //catch file upload Here
        if(filetoupload === "" || filetoupload === null || filetoupload === undefined){}
        //catch post data in here


        //post data and file to server


        //delete current request sent from consultant requests
        console.log('hi')
    }
    return (
        <div className="cprofile">
            <h1>Hello from Consultant DashBoard</h1>
            {/*Received Here*/}
            <div className="myDiv">
                <br />
                <br />
                <h1>Requests Received</h1>
                {displayconsultantrequests()}
                <br />
                <br />
            </div>

            <br></br>

            {/*Responds  Here*/}
            <div className="myDiv">
                <br />
                <br />
                <h1>Completed</h1>
                <table id="customers">
                    <tbody>
                        <tr>
                            <th>Number</th>
                            <th>User</th>
                            <th>Topic</th>
                            <th>Content</th>
                            <th>Date</th>
                        </tr>
                        <tr>
                            <td>03</td>
                            <td>John Doe</td>
                            <td>Some Topic</td>
                            <td>Some Content</td>
                            <td>Date</td>
                        </tr>
                    </tbody>
                </table>
                <br />
                <br />
            </div>

        </div>
    )
}
