import React, { useState, useRef } from 'react';
import './Footer.css';
// import { Button } from './Button';
import { Link } from 'react-router-dom';
// import Overlay from 'react-bootstrap/Overlay'
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'
// import Tooltip from 'react-bootstrap/Tooltip'
import Popover from 'react-bootstrap/Popover'
// import PopoverContent from 'react-bootstrap/PopoverContent'
// import PopoverTitle from 'react-bootstrap/PopoverTitle'

function Footer() {
  // const [show, setShow] = useState(false);
  // const [target, setTarget] = useState(null);
  // const ref = useRef(null);

  // const handleClick = (event) => {
  //   setShow(!show);
  //   setTarget(event.target);
  // };


  return (
    <div className='footer-container'>
      <section className='footer-subscription'>
        <p className='footer-subscription-heading'>
          Join the Financial Advisor newsletter to receive our Updates
        </p>
        <p className='footer-subscription-text'>
          You can unsubscribe at any time.
        </p>
        <div className='input-areas'>
          <form>
            <input
              className='footer-input'
              name='email'
              type='email'
              placeholder='Your Email'
            />
            <button className='btn--outline'>Subscribe</button>
          </form>
        </div>
      </section>
      <div className='footer-links'>
        <div className='footer-link-wrapper'>
          <div className='footer-link-items'>
            <h2>About Us</h2>
            <Link to='/sign-up'>How it works</Link>
            <Link to='/'>Testimonials</Link>
            <Link to='/'>Careers</Link>
            <Link to='/'>Investors</Link>
            <Link to='/'>Terms of Service</Link>
          </div>
          <div className='footer-link-items'>
            <h2>Development Team</h2>
            {['right'].map((placement) => (
              <OverlayTrigger
                trigger="click"
                key={placement}
                placement={placement}
                overlay={
                  <Popover id={`popover-positioned-${placement}`}>
                    <Popover.Title as="h3">{`Professor:`}</Popover.Title>
                    <Popover.Content>
                      Anjana Shah
                    </Popover.Content>
                  </Popover>
                }
              >
                <button className='btn--outline'>Project Manager</button>
              </OverlayTrigger>
            ))}
            <br />
            {['right'].map((placement) => (
              <OverlayTrigger
                trigger="click"
                key={placement}
                placement={placement}
                overlay={
                  <Popover id={`popover-positioned-${placement}`}>
                    <Popover.Title as="h3">{`Developers:`}</Popover.Title>
                    <Popover.Content>
                      <p>Sureya Farah</p>
                      <p>Adrian Filipe</p>
                      <p>Sheak Aftakhar Rahman</p>
                      <p>Kanta Husari</p>
                    </Popover.Content>
                  </Popover>
                }
              >
                <button className='btn--outline'>Team</button>
              </OverlayTrigger>
            ))}
            <br />
            <a href='https://www.georgebrown.ca/' rel="noreferrer" target="_blank">George Brown College</a>
          </div>
        </div>
        <div className='footer-link-wrapper'>
        </div>
      </div>
      <section className='social-media'>
        <div className='social-media-wrap'>
          <div className='footer-logo'>
            <h3 className='social-logo'>
              Financial Advisor Â© 2021
            </h3>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Footer;
