import React from 'react'
import axios from "axios"
// import Balance_Sheet from '../Components/Resources/Resume_2021.pdf';
// D:\MyStuff\George Brown\Year 3\2 Semester\CAPSTONE PROJECT 02\Github\CapStone-FA\BackEnd\ServiceData\Balance_Sheet.xlsx
export default function Update() {
    function getFileToDownload (apiUrl) {
        return axios.get(apiUrl, {
          responseType: 'arraybuffer',
          headers: {
            'Content-Type': 'application/json'
          }
        })
     }
     getFileToDownload('http://localhost:8080/ServiceData\Balance_Sheet.xlsx')
     .then (response => {
         const type = response.headers['content-type']
         const blob = new Blob([response.data], { type: type, encoding: 'UTF-8' })
         const link = document.createElement('a')
         link.href = window.URL.createObjectURL(blob)
         link.download = 'Balance_Sheet.xlsx'
         link.click()
     })

    function submitHandler() {
        console.log("submit")
    }


    return (
        <div className="profile">
            <h1>Hello from Update</h1>
            <div className="myDiv">
                <br />
                <br />
                {/*----------------------------------------*/}
                <h1>1- Step One: Select a template to download</h1>
                <div className="col-sm-6 offset-sm-3">
                    {/*to be  changed later*/}
                    <form onSubmit={submitHandler}>
                        <select >
                            <option >Select an Option</option>
                            <option value="cash">Cash Report</option>
                            <option value="balancesheet">Balance Sheet</option>
                            <option value="cashflow">Cash Flow</option>
                        </select>
                        <input type="submit" value="Download" className="btn btn-primary" />
                    </form>
                </div>
                {/*----------------------------------------*/}

                <br />
                <br />
            </div>
            <br />
            <div className="myDiv">
                <br />
                <br />
                <h1>2- Step Two: Fill up the template and upload</h1>
                
                
                <div className="col-sm-6 offset-sm-3">
                <form onSubmit={submitHandler}>
                <select >
                            <option >Select an Option</option>
                            <option value="cash">Cash Report</option>
                            <option value="balancesheet">Balance Sheet</option>
                            <option value="cashflow">Cash Flow</option>
                        </select>
                    <input type="file" id="myFile" name="filename" />
                    <input type="submit" className="btn btn-primary"/>
                </form>
                </div>

                <br />
                <br />
            </div>
        </div>
    )
}
