import React, { useState, useEffect } from 'react'
import "./ManagerStyles.css"
import axios from "axios"
import moment from 'moment'
export default function DashboardManagerDetails() {

    const [usersRequests, setusersRequests] = useState([])
    const [consultantList, setconsultantList] = useState([])
    const [pageRefresh, setpageRefresh] = useState(0)
    const [seconds, setSeconds] = useState(0);
    const [ForwardTo, setForwardTo] = useState("")

    useEffect(() => {
        const interval = setInterval(() => {
            //-----------------------------------------
            axios.post("http://localhost:8080/managerrequests", "")
                .then(res => {
                    const resUsersRequests = res.data
                    setusersRequests([...resUsersRequests])
                    // console.log(usersRequests)
                })

            axios.post("http://localhost:8080/consultants", "")
                .then(res => {
                    const consult = res.data
                    setconsultantList([...consult])
                    // console.log(consultantList)
                })
            //-----------------------------------------
            setSeconds(seconds => seconds + 1);
        }, 1000);
        return () => clearInterval(interval);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [pageRefresh, ForwardTo])

    // pageRefresh, ForwardTo

    // function displayConsults() {
    //     return (
    //         <select
    //             // value={ForwardTo}
    //             onChange={e => setForwardTo(e.target.value)}
    //         >
    //             <option value="">Select Consultatnt</option>
    //             {
    //                 consultantList.map(
    //                     item => <option key={item._id} value={item._id}>{`${item.first_name} ${item.last_name}`}</option>
    //                 )
    //             }
    //         </select>
    //     )
    // }
    function displayUserRequests() {
        if (usersRequests.length === 0) {
            return (
                <h1>NO requests to show</h1>
            )
        } else {
            return (
                //-----------------------------------------
                <table id="customers">
                    <tbody>
                        <tr>
                            <th>User</th>
                            <th>Topic</th>
                            <th>Urgency</th>
                            <th>Forward To</th>
                            <th>Date</th>
                            <th>Action</th>
                        </tr>
                        {
                            usersRequests.map(
                                item =>
                                    /*item.id is request ID*/
                                    <tr key={item._id}>
                                        <td>{item.user_name}</td>
                                        <td>{item.topic}</td>
                                        <td>{item.urgency}</td>
                                        {/* <td>{displayConsults()}</td> */}
                                        <td>
                                            <select
                                                // value={ForwardTo}
                                                className='DropList'
                                                onChange={
                                                    (e) => {
                                                        // setForwardTo({ row: item._id, option: e.target.value })
                                                        // setSeconds(seconds => seconds + 1);
                                                        // console.log(ForwardTo)
                                                        // return ForwardTo
                                                        return (ForwardTo)
                                                    }
                                                }
                                            >
                                                <option value="">Select Consultatnt</option>
                                                {
                                                    consultantList.map(
                                                        consult => <option key={consult._id} value={consult._id}>{`${consult.first_name} ${consult.last_name}`}</option>
                                                    )
                                                }
                                            </select>
                                        </td>
                                        <td>{moment(item.created).format("LLL")}</td>
                                        <td><button onClick={() => postfunction(item)} className="btn btn-primary">Post</button></td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table >
                //-----------------------------------------
            )
        }
    }
    function selectOnchange(state) {
        setForwardTo([{
            row: state.row,
            option: state.option
        }])

        // console.log(ForwardTo)

    }

    // function resetOptions(){
    //     document.getElementById("DropList").nodeValue= "0";
    // }

    function postfunction(item) {
        // console.log(`this is item ${item}`)
        // console.log(item)
        const row = {
            consultant: ForwardTo.option,
            user: item.user_name,
            topic: item.topic,
            urgency: item.urgency,
            description: item.description,
            user_email: item.email,
            originalrow: ForwardTo.row,
            // date: moment(item.created).format("LLL"),
        }

        // console.log(`this is row ${row}`)
        // console.log(row)
        if (row.originalrow === "" || row.consultant === "") {
            window.alert("Please select an option from forward to")
        } else {
            if (row.originalrow !== row.row_id) {
                window.alert("Please select an option from forward to")
                setForwardTo("")
            } else {
                console.log(row)
                // axios.post("http://localhost:8080/toconsultantrequest", row)
                //     .then(res => {
                //         console.log(res)
                //     })
                //send the report and delete it from the list
                setForwardTo("")
            }
        }
    }

    return (
        <div className="mprofile">
            <h1>Hello from Manager DashBoard</h1>
            {/*Received Here*/}
            <div className="myDiv">
                <br />
                <br />
                <h1>Requests Received</h1>
                {displayUserRequests()}
                <br />
                <br />
            </div>

            <br />
            {/*Pending  Here*/}
            <div className="myDiv">
                <br />
                <br />
                <h1>Requests Pending</h1>
                <table id="customers">
                    <tbody>
                        <tr>
                            <th>Number</th>
                            <th>User</th>
                            <th>Status</th>
                            <th>Topic</th>
                            <th>Date</th>
                        </tr>
                        <tr>
                            <td>02</td>
                            <td>John Doe</td>
                            <td>Pending</td>
                            <td>Some Topic</td>
                            <td>Date</td>
                        </tr>
                    </tbody>
                </table>
                <br />
                <br />
            </div>

            <br></br>

            {/*Responds  Here*/}
            <div className="myDiv">
                <br />
                <br />
                <h1>Responds</h1>
                <table id="customers">
                    <tbody>
                        <tr>
                            <th>Number</th>
                            <th>User</th>
                            <th>Status</th>
                            <th>Topic</th>
                            <th>Date</th>
                        </tr>
                        <tr>
                            <td>03</td>
                            <td>John Doe</td>
                            <td>Complete</td>
                            <td>Some Topic</td>
                            <td>Date</td>
                        </tr>
                    </tbody>
                </table>
                <br />
