import React from 'react'
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
const authentication = {
    isLoggedIn: false,
    onAuthentication() {
        this.isLoggedIn = true
    },
    getLoginStatus() {
        return this.isLoggedIn
    }
}
function Auth(props) {
    return (
        <Route path={props.path} render={data => authentication.getLoginStatus ? (<props.component{...data}></props.component>) :
            (<Redirect to="/"></Redirect>)}></Route>
    )
}

export default Auth


