import React, { useState, useEffect } from 'react'
import axios from "axios"
import moment from 'moment'
import dashBoardstyle from "../User/dashBoardstyle.css"
import Spinner from 'react-bootstrap/Spinner'
// export default function DashboardUserDetails() {
class DashboardUserDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            serverEmail: "",
            balanceSheetData: [],
            isLoading: true
        };
    }
    componentWillMount() {
        this.renderMyData();
    }
    renderMyData() {
        const user = {
            "email": localStorage.getItem("user"),
        }
        axios.post("http://localhost:8080/getuserbalances", user)
            .then(res => {
                this.setState({
                    serverEmail: res.data.email,
                    balanceSheetData: [...res.data],
                    isLoading: false
                });
                console.log(this.state.balanceSheetData[0].year)
                return this.state.balanceSheetData
            })
    }

    render() {
        if (this.state.isLoading) {
            return (
                // <div className="fa fa-spinner fa-spin">Loading ...</div>
                <Spinner animation="border" role="status">
                    <span className="sr-only">Loading...</span>
                </Spinner>
            )
        } else {
            const balanceSheetData=this.state.balanceSheetData
            console.log()
            return (
                <div className="dashboardBody" >
                    <h1>{`${balanceSheetData[balanceSheetData.length-1].year} Major Indicators`}</h1>
                    <main className="main">
                        <div className="square A">{`${balanceSheetData[0].year}`}</div>
                        <div className="square B">B</div>
                        <div className="square C">C</div>
                        <div className="square D">D</div>
                        <div className="square E">E</div>
                    </main>
                    <main className="main">

                        <div className="square A2">A
                            <table>
                                <tbody>
                                    <tr className="Horizontal-head">
                                        <th>1</th>
                                        <th>22</th>
                                        <th>33</th>
                                        <th>44</th>
                                        <th>55</th>
                                    </tr>
                                    <tr>
                                        <th>2</th>
                                        <td>4</td>
                                        <td>6</td>
                                        <td>8</td>
                                        <td>10</td>
                                    </tr>
                                    <tr>
                                        <th>2</th>
                                        <td>4</td>
                                        <td>6</td>
                                        <td>8</td>
                                        <td>10</td>
                                    </tr>
                                    <tr>
                                        <th>3</th>
                                        <td>6</td>
                                        <td>9</td>
                                        <td>12</td>
                                        <td>15</td>
                                    </tr>
                                    <tr>
                                        <th>4</th>
                                        <td>8</td>
                                        <td>12</td>
                                        <td>16</td>
                                        <td>20</td>
                                    </tr>
                                    <tr>
                                        <th>5</th>
                                        <td>10</td>
                                        <td>15</td>
                                        <td>20</td>
                                        <td>25</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>

                    </main>
                </div>
            )
        }


    }
}

export default DashboardUserDetails
