import React, { useState, useEffect } from 'react'
import "./ManagerStyles.css"
import axios from "axios"
import moment from 'moment'
export default function DashboardManagerDetails() {

    const [usersRequests, setusersRequests] = useState([])
    const [pendingRequest, setpendingRequest] = useState([])
    const [consultantList, setconsultantList] = useState([])
    const [pageRefresh, setpageRefresh] = useState(0)
    const [seconds, setSeconds] = useState(0);
    const [ForwardTo, setForwardTo] = useState("")

    useEffect(() => {
        const interval = setInterval(() => {
            //-----------------------------------------
            //get requests
            axios.post("http://localhost:8080/managerrequests", "")
                .then(res => {
                    const resUsersRequests = res.data
                    setusersRequests([...resUsersRequests])
                    // console.log(usersRequests)
                })

            axios.post("http://localhost:8080/findAllconsultantrequests", "")
                .then(res => {
                    const resconsultantsRequests = res.data
                    setpendingRequest([...resconsultantsRequests])
                    // console.log(resconsultantsRequests)
                })

            axios.post("http://localhost:8080/consultants", "")
                .then(res => {
                    const consult = res.data
                    setconsultantList([...consult])
                    // console.log(consultantList)
                })
            //-----------------------------------------
            setSeconds(seconds => seconds + 1);
        }, 1000);
        return () => clearInterval(interval);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [pageRefresh])


    // pageRefresh, ForwardTo

    // function displayConsults() {
    //     return (
    //         <select
    //             // value={ForwardTo}
    //             onChange={e => setForwardTo(e.target.value)}
    //         >
    //             <option value="">Select Consultatnt</option>
    //             {
    //                 consultantList.map(
    //                     item => <option key={item._id} value={item._id}>{`${item.first_name} ${item.last_name}`}</option>
    //                 )
    //             }
    //         </select>
    //     )
    // }
    function displayUserRequests() {
        if (usersRequests.length === 0) {
            return (
                <h1>NO requests to show</h1>
            )
        } else {
            return (
                //-----------------------------------------
                <table id="customers">
                    <tbody>
                        <tr>
                            <th>User</th>
                            <th>Topic</th>
                            <th>Urgency</th>
                            <th>Forward To</th>
                            <th>Date</th>
                            <th>Action</th>
                        </tr>
                        {
                            usersRequests.map(
                                item =>
                                    /*item.id is request ID*/
                                    <tr key={item._id}>
                                        <td>{item.user_name}</td>
                                        <td>{item.topic}</td>
                                        <td>{item.urgency}</td>
                                        {/* <td>{displayConsults()}</td> */}
                                        <td>
                                            <select
                                                // value={ForwardTo}
                                                className='DropList'
                                                onChange={
                                                    (e) => {
                                                        setForwardTo({ row: item._id, option: e.target.value })
                                                        return (ForwardTo)
                                                    }
                                                }
                                            >
                                                <option value="">Select Consultatnt</option>
                                                {
                                                    consultantList.map(
                                                        consult => <option key={consult._id} value={consult._id}>{`${consult.first_name} ${consult.last_name}`}</option>
                                                    )
                                                }
                                            </select>
                                        </td>
                                        <td>{moment(item.created).format("LLL")}</td>
                                        <td><button onClick={() => postfunction(item)} className="btn btn-primary">Post</button></td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table >
                //-----------------------------------------
            )
        }
    }
    function displayPendingrRequests() {
        //         consultant: "603a72704ac2f68378f66c3b"
        // date: "2021-03-25T10:56:55.304Z"
        // description: "please send me cash report"
        // originalrow: "605c41999bec593ca0f4029a"
        // topic: "2020 cash"
        // urgency: "urgent"
        // user: "Jane Doe"
        // user_email: "jane@doe.ca"
        if (pendingRequest.length === 0) {
            return (
                <h1>NO requests pending</h1>
            )
        } else {
            return (
                <table id="customers">
                    <tbody>
                        <tr>
                            <th>User</th>
                            <th>Topic</th>
                            <th>Urgency</th>
                            <th>Forward To</th>
                            <th>Date</th>
                            <th>Status</th>
                        </tr>
                        {
                            pendingRequest.map(
                                data =>
                                    <tr key={data._id}>
                                        <td>{data.user}</td>
                                        <td>{data.topic}</td>
                                        <td>{data.urgency}</td>
                                        <td>{
                                        
                                        }</td>
                                        <td>{moment(data.date).format("LLL")}</td>
                                        <td>pending</td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table >
            )
        }
    }

    function postfunction(item) {
        const row = {
            consultant: ForwardTo.option,
            user: item.user_name,
            topic: item.topic,
            urgency: item.urgency,
            description: item.description,
            user_email: item.email,
            originalrow: ForwardTo.row,
            // date: moment(item.created).format("LLL"),
