module.exports = {
    isLoggedIn: false,
    doLogin() {
        while (!this.doLogout()) {
            this.isLoggedIn = true
        }
    },
    getLoginStatus() {
        return this.isLoggedIn
    },
    doLogout() {
        this.isLoggedIn = false
    }
}
