import React, { useState } from 'react'
import "../User/UserStyles.css"
//Modal
import Modal from "react-modal"

export default function Profile() {
    //update profile MOdal
    const [profilemodal, setProfilemodal] = useState(false)
    const [addcardmodal, setAddcardmodal] = useState(false)

    //Modal functionality
    //Profile Modal
    function closeAndUpdateProfile() {
        setProfilemodal(false)
    }

    function openToUpdateProfile() {
        setProfilemodal(true)
    }


    //card modal
    function closeAddCard() {
        setAddcardmodal(false)
    }

    function openAddCard() {
        setAddcardmodal(true)
    }

    return (
        <div>

            <div className="profile">
                <h1>Hello from profile</h1>
                <div className="myDiv">
                    <br />
                    <br />
                    <h1>User Name: Here</h1>
                    <h1>Email: Here</h1>
                    <h1>Password: Here</h1>
                    <button className="btn btn-primary" onClick={openToUpdateProfile}>Edit Profile</button>
                    <br />
                    <br />
                </div>
                <br />
                <div className="myDiv">
                    <br />
                    <br />
                    <h1>Cards: Here</h1>
                    <button className="btn btn-primary" onClick={openAddCard}>Add Card</button>
                    <br />
                    <br />
                </div>

            </div>


            {/*Modals you can change styles of the modal in here*/}
            {/*Profile*/}
            <div>
                <Modal
                    isOpen={profilemodal}
                    onRequestClose={() => setProfilemodal(false)}
                    style={
                        {
                            overlay: {
                                backgroundColor: 'azure',
                            },
                            content: {
                                color: 'MenuText',
                                width: '50%',
                                height: '50%',
                                top                   : '50%',
                                left                  : '50%',
                                right                 : 'auto',
                                bottom                : 'auto',
                                marginRight           : '-50%',
                                transform             : 'translate(-50%, -50%)'
                            },

                        }
                    }
                >
                    <h1>Update Profile</h1>
                    <p>Add Stuff Here</p>
                    <button onClick={closeAndUpdateProfile} className='btn btn-primary profile-modal-button'>Update</button>
                </Modal>

                {/*Card*/}

                <Modal 
                isOpen={addcardmodal}
                onRequestClose={() => setAddcardmodal(false)}
                style={
                    {
                        overlay: {
                            backgroundColor: 'grey',
                        },
                        content: {
                            color: 'MenuText',
                            width: '50%',
                            height: '50%',
                            top                   : '50%',
                            left                  : '50%',
                            right                 : 'auto',
                            bottom                : 'auto',
                            marginRight           : '-50%',
                            transform             : 'translate(-50%, -50%)'
                        },
                    }
                }
                >
                    <h1>Update Card</h1>
                    <p>Add Stuff Here</p>
                    <button onClick={closeAddCard}className='btn btn-primary profile-modal-button'>Add Card</button>
                </Modal>
            </div>



        </div>
    )
}
